var http = require('http');
var url = require('url');
var conf = require('./config');
var wechat = require('../simple-wechat');

http.createServer(function(req, res) {
	var urlinfo = url.parse(req.url);
	if (urlinfo.pathname == '/api') {
		new wechat.Response(req, res, conf.token, function(err, msg, wxres) {
			wxres.reply('hello world');
		});
	}
}).listen(conf.port);
